                        onChange={(e) => {
                        this.setState({ value6: e.target.value })
                        }} 
                        onkeydown="return event.keyCode !== 69"/></td>
            <td><input  type="number" name="c7"
                        value={this.state.value7}
                        onChange={(e) => {
                        this.setState({ value7: e.target.value })
                        }} 
                        onkeydown="return event.keyCode !== 69"/></td>

          </tr>
        </tbody>
      </table>

      <div className="Div"></div>

         <table className="user" >
        <thead>
          <tr> 
            <th><b>c00</b></th> 
            <th><b>c11</b></th> 
