            <th><b>c10</b></th> 
            </tr>
        </thead>
        <tbody>
          <tr>

            <td><input  type="number" name="c00" 
                        value={this.state.value00}
                        onChange={(e) => {
                        this.setState({ value00: e.target.value })
                        }}
                        onkeydown="return event.keyCode !== 69" /></td>
            <td><input  type="number" name="c11"
                        value={this.state.value11}
